import './App.css';

import {BrowserRouter,Routes,Route,Link} from 'react-router-dom'
import MyShift from './Pages/MyShift';
import AvailableShift from './Pages/AvailableShift';
import { useState } from 'react';
function App() {
  const [activeLink,setactiveLink]=useState("/MyShift");
  const handleClick=(link)=>{
    setactiveLink(link);

  }
  return (
    <BrowserRouter>
     <nav className='App'>
      <div id='Nav'><Link to="/MyShift" onClick={()=>handleClick('/AvailableShift')} className={activeLink==='/MyShift'?"active":" "}>My Shift </Link></div>

      <div id='Nav'><Link onClick={()=>handleClick('/AvailableShift')} to="/AvailableShift" className={activeLink==='/AvailableShift'?"active":" "}> Available Shift</Link></div>
     </nav>
     <Routes>
      <Route path="/MyShift" element={<MyShift/>}/>
      <Route path="/AvailableShift" element={<AvailableShift/>}/>
     </Routes>
    </BrowserRouter>

  );
}

export default App;
